from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.tools import wait
from urandom import randint

# Set up all devices.
wall = Motor(Port.A, Direction.CLOCKWISE)


# The main program starts here.
# Find edge.
wall.run_until_stalled(-500, Stop.COAST, 50)
wall.reset_angle(-180)
# Move at random speed to random position.
while True:
    if randint(0, 1):
        wall.run_target(randint(300, 1000), randint(0, 1000), Stop.COAST)
    else:
        wall.run_target(randint(300, 1000), randint(2100, 2900), Stop.COAST)
    wait(randint(500, 2000))
